package de.nukez.main;import events.JoinQuitListener;import gamemanager.GameState;import mysql.MySQL;import mysql.MySQLFile;import mysql.MySQLManager;import org.bukkit.Bukkit;import org.bukkit.plugin.PluginManager;import org.bukkit.plugin.java.JavaPlugin;public class Main extends JavaPlugin {    public static final String prefix = "ยง3Varo ยง7| ยงr";    private static Main plugin;    private MySQL mySQL;    private MySQLFile mySQLFile;    private GameState gameState;    @Override    public void onEnable() {        plugin = this;        registerEvents();        gameState = GameState.WAITING;        setGameState(MySQLManager.getMySQlGame());        mySQLFile = new MySQLFile();        mySQL = new MySQL();        if (MySQLManager.getMySQlGame().equals(GameState.SETUP)){        }    }    @Override    public void onDisable() {        if (mySQL.isConnected()){            MySQLManager.setNextState();            mySQL.disconnect();        }    }    public MySQLFile getMySQLFile() {        return mySQLFile;    }    public MySQL getMySQL() {        return mySQL;    }    public static Main getPlugin() {        return plugin;    }    private void registerEvents() {        PluginManager pm = Bukkit.getPluginManager();        pm.registerEvents(new JoinQuitListener(), this);    }    public GameState getGameState() {        return gameState;    }    public void setGameState(GameState gameState) {        this.gameState = gameState;    }}