package events;import org.bukkit.entity.Player;import org.bukkit.event.Listener;import org.bukkit.event.player.PlayerJoinEvent;import org.bukkit.event.player.PlayerQuitEvent;public class JoinQuitListener implements Listener {    public void onJoin(PlayerJoinEvent e) {        Player p = e.getPlayer();        e.setJoinMessage(null);    }    public void onQuit(PlayerQuitEvent e) {        e.setQuitMessage(null);    }}